import { useEffect, useRef, useState } from "react";

//the final bottom
function Username({setIsReady,setVal}) {
  //get the input
  const nameRef = useRef(null);
  //booleans object, to search in other users
  const [isNameExist, setIsNameExist] = useState(false);

  //check the no other user uses this username
  const checkOtherUserNames = () => {
    const inputName = nameRef.current.value;
    try{
      const res = async() => await fetch('http://localhost:5000/api/Users/' + string(nameRef), {
method: 'get', // send a get request
headers: {
'Content-Type': 'application/json', // the data (username/password) is in the form of a JSON object
}
});
if (res.status != 200)
setIsNameExist(false);
else {
  setIsNameExist(true);
}
    }
    catch(error){
      console.error('An error occurred:', error);
    }   
  };

  //check if ready to submit
  const isReady = () => {
    const name = nameRef.current.value
    if(!isNameExist && name != ''){
      setIsReady(true);
      setVal(name);
    }else{
      setIsReady(false);
    }

  }
  


  return (
    <>
      <div className="lable">
        <div className="input-group mb-3">
          <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon1">
              Username
            </span>
          </div>
          <input
            ref={nameRef}
            onKeyUp={() => {
              checkOtherUserNames();
              isReady();
            }}
            type="text"
            className="form-control"
            aria-label="Username"
            aria-describedby="basic-addon1"
          />
        </div>
        {isNameExist && (
          <div className="lable alert alert-danger">
            This username already exists, please pick another one
          </div>
        )}
      </div>
    </>
  );
}

export default Username;
