import React, { useState, useEffect, useRef } from "react";
import ProfilePic from "./ProfilePic.js";
import ChatButtons from "./ChatButtons.js";
import Message from "./Message.js";
import SendMessage from "./SendMessage.js";


function ChatBox({chat, user, selectedContact, setChat, updateChatMessages, handleDeleteChat}) {
  const [chatMessages, setChatMessages] = useState({});
  const messagesContainerRef = useRef(null);
  const messages = chat ? chat.messages || [] : [];

  useEffect(() => {
    if (chat) {
      setChatMessages((prevMessages) => ({
        ...prevMessages,
        [chat]: {
          messages: chatMessages || [],
        },
      }));
    }
  }, [chat]);



  useEffect(() => {
    const container = messagesContainerRef.current;
    if (container && chat) {
      container.scrollTop = container.scrollHeight;
    }
  }, [chat]);

  const formatDateTime = (dateTime) => {
    const options = {
      year: "numeric",
      month: "numeric",
      day: "numeric",   
      hour: "numeric",
      minute: "numeric",
      second: "numeric",
      hour12: false,
    };
    return dateTime.toLocaleString("en-US", options);
  };

  async function handleSendMessage (messageText) {
    if (chat) {
      const msg = { msg: messageText };
      const id = chat.id
      const res = await fetch(`http://localhost:5000/api/Chats/${id}/Messages`, {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        'body': JSON.stringify(msg)
      });
      if (res.status != 200){
        const errorMessage = await res.text();
        alert(res.status + " " + res.statusText + "\n" + errorMessage);
      } else {
        const data = await res.json();
        const newMessage = {
          id: data.id,
          created: data.created,
          sender:
          {
            "username": data.sender.username,
            "displayName": data.sender.displayName,
            "profilePic": data.sender.profilePic
          },
          content: data.content
        }

        const updatedMessages = [
          ...(chat.messages || []),
          newMessage,
        ];

        const updatedChat = {
          ...chat,
          messages: updatedMessages,
          lastMessage: newMessage
        };

        setChatMessages((prevMessages) => ({
          ...prevMessages,
          [chat]: {
            messages: updatedMessages,
            lastMessage: newMessage
          },
        }));
        updateChatMessages(chat.id, updatedMessages);
        setChat(updatedChat);
      }
    }    
  }
  useEffect(() => {
    if (chat) {
      console.log(chat.lastMessage);
    }
  }, [chat]);

  return (
    <div id="chat_window">
      {selectedContact && (
        <>

          <ProfilePic pic={selectedContact.profilePic}/>
          <span className="username">{selectedContact.displayName}</span>
        </>
      )}
      <ChatButtons chat={chat} handleDeleteChat={handleDeleteChat}/>
      <div id="messages" ref={messagesContainerRef}>
      {messages.slice().reverse().map((message, index) => {
        const incoming = message.sender.username === user.username ? 0 : 1;
        const dateTime = new Date(message.created);
        const date = dateTime.toLocaleDateString();
        const time = dateTime.toLocaleTimeString();
        const dateTimeFormatted = `${date} ${time}`;
        return (
            <Message
            key={index}
            text={message.content}
            time={dateTimeFormatted}
            incoming={incoming}
            />
        );
        })}
      </div>
      <SendMessage
        onSendMessage={handleSendMessage}
        selectedContact={selectedContact ? selectedContact.username : null}
      />
    </div>
  );
}

export default ChatBox;
